import React, { useState , useEffect} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import './admin.css';

const Admin = () => {
    const [tableData, setTableData] = useState([]);

    const getList = async () => {
        try {
          const response = await axios.get("http://localhost:4000/kostum");
          setTableData(response.data.data);
        } catch (error) {
          console.log(error);
        }
    }
      // komponen dirender pertama kali
      useEffect(() => {
        getList();
      }, [])

    const [isPopupVisible, setPopupVisible] = useState(false);
    const [editedItem, setEditedItem] = useState({
        id: null,
        nama: "",
        gambar: "",
        deskripsi: "",
        harga: "",
        status: ""
    });

    const handleAddButtonClick = () => {
        setEditedItem({
            id: '',
            nama: '',
            gambar: '',
            deskripsi: '',
            harga: '',
            status: '',
        });
        setPopupVisible(true);
    };

    const handleClosePopup = () => {
        setPopupVisible(false);
    };

    const handleEdit = async (id) => {
        try {
            const response = await axios.get(`http://localhost:4000/kostum/${id}`);
            setEditedItem(response.data.data);
            setPopupVisible(true);
        } catch (error) {
            console.log(error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:4000/kostum/delete/${id}`);
            const updatedTableData = tableData.filter((item) => item.id !== id);
            setTableData(updatedTableData);
        } catch (error) {
            console.log(error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedItem({
            ...editedItem,
            [name]: value,
        });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
    
        try {
            if (editedItem.id === null || editedItem.id === '') {
                // Send a POST request for adding a new item
                await axios.post('http://localhost:4000/kostum/add', {
                    nama: editedItem.nama,
                    gambar: editedItem.gambar,
                    deskripsi: editedItem.deskripsi,
                    harga: editedItem.harga,
                    status: editedItem.status,
                });
            } else {
                // Send a PATCH request for updating an existing item
                await axios.patch(`http://localhost:4000/kostum/edit/${editedItem.id}`, editedItem);
            }
    
            // Update the table data after the request is successful
            getList();
        } catch (error) {
            console.log(error);
        }
    
        handleClosePopup();
    };
    
    
    const handleTextareaResize = (e) => {
        const textarea = e.target;
        textarea.style.height = 'auto';
        textarea.style.height = `${textarea.scrollHeight}px`;

        const popupContent = textarea.closest('.popup-content');
        if (popupContent) {
            const maxHeight = parseInt(getComputedStyle(popupContent).maxHeight, 10);
            if (textarea.scrollHeight > maxHeight) {
                popupContent.style.height = `${maxHeight}px`;
            } else {
                popupContent.style.height = 'auto';
            }
        }
    };

    return (
        <div className="body">
            <header className="header">
                <div className="left-section">
                    <h1 className='admin-dashboard'>Admin Dashboard</h1>
                </div>
                <div className="right-section">
                    <div className="search-container">
                        <input type="text" placeholder="Search..." />
                        <FontAwesomeIcon icon={faSearch} className='icon-search'/>
                    </div>
                    <button className="add-button" onClick={handleAddButtonClick}>
                        Tambah Barang
                    </button>
                </div>
            </header>
            <div>
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Nama</th>
                            <th>Gambar</th>
                            <th>Deskripsi</th>
                            <th>Harga</th>
                            <th>Status</th>
                            <th>Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tableData.map((item) => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.nama}</td>
                                <td>
                                    <img src={item.gambar} alt={item.nama} className="table-image" />
                                </td>
                                <td>{item.deskripsi.slice(0, 20)}{item.deskripsi.length > 20 ? '...' : ''}</td>
                                <td>{`Rp. ${item.harga}`}</td>
                                <td>{item.status}</td>
                                <td className="action-column">
                                    <div className="action-buttons">
                                        <div onClick={() => handleEdit(item.id)}>
                                            <FontAwesomeIcon icon={faEdit} className='action-icon' />
                                            <span>Edit</span>
                                        </div>
                                        <div onClick={() => handleDelete(item.id)}>
                                            <FontAwesomeIcon
                                                icon={faTrash}
                                                className='action-icon'
                                            />
                                            <span>Delete</span>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {isPopupVisible && (
                <div className="popup">
                    <div className="popup-content">
                        <h2>{editedItem.id ? 'Edit' : 'Tambah'} Barang</h2>
                        <form onSubmit={handleFormSubmit}>

                            <label>Nama:</label>
                            <input
                                type="text"
                                name="nama"
                                value={editedItem.nama}
                                onChange={handleInputChange}
                                required
                            />

                            <label>Gambar:</label>
                            <input
                                type="text"
                                name="gambar"
                                value={editedItem.gambar}
                                onChange={handleInputChange}
                                required
                            />

                            <label>Deskripsi:</label>
                            <textarea
                                name="deskripsi"
                                value={editedItem.deskripsi}
                                onChange={handleInputChange}
                                onInput={handleTextareaResize}
                                style={{ minHeight: '80px', resize: 'vertical', overflowY: 'auto' }}
                                required
                            />

                            <label>Harga:</label>
                            <div className="input-with-prefix">
                                <span>Rp.</span>
                                <input
                                    type="text"
                                    name="harga"
                                    value={editedItem.harga}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>

                            {editedItem.id && (
                                <>
                                    <label>Status:</label>
                                    <select
                                        name="status"
                                        value={editedItem.status}
                                        onChange={handleInputChange}
                                        required
                                    >
                                        <option value="Tersedia">Tersedia</option>
                                        <option value="Tidak Tersedia">Tidak Tersedia</option>
                                    </select>
                                </>
                            )}

                            <button type="submit">Submit</button>
                        </form>

                        <button onClick={handleClosePopup} className="close-button">
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Admin;
